.ph-section-full {
    min-height: 50vh;
    background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 4rem 2rem;
    position: relative;
    overflow: hidden;
    border-top: 1px solid #e2e8f0;
    border-bottom: 1px solid #e2e8f0;
}

.ph-section-full::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 70% 30%, rgba(99, 102, 241, 0.08) 0%, transparent 50%),
                radial-gradient(circle at 30% 70%, rgba(168, 85, 247, 0.05) 0%, transparent 50%);
    pointer-events: none;
}

.ph-container {
    max-width: 1200px;
    width: 100%;
    text-align: center;
    position: relative;
    z-index: 2;
}

.ph-header {
    margin-bottom: 3rem;
    animation: fadeInUp 1s ease-out 0.2s both;
}

.ph-icon-wrapper {
    position: relative;
    display: inline-block;
    margin-bottom: 2rem;
}

.ph-rocket-icon {
    width: 64px;
    height: 64px;
    color: #6366f1;
    animation: rocketLaunch 3s ease-in-out infinite;
    filter: drop-shadow(0 4px 8px rgba(99, 102, 241, 0.3));
}

.ph-particles {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    pointer-events: none;
}

.particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: linear-gradient(45deg, #6366f1, #a855f7);
    border-radius: 50%;
    animation: particleFloat 2s ease-in-out infinite;
}

.particle:nth-child(1) {
    top: -30px;
    left: -20px;
    animation-delay: 0s;
}

.particle:nth-child(2) {
    top: -25px;
    left: 15px;
    animation-delay: 0.4s;
}

.particle:nth-child(3) {
    top: -35px;
    left: -5px;
    animation-delay: 0.8s;
}

.particle:nth-child(4) {
    top: -20px;
    left: -25px;
    animation-delay: 1.2s;
}

.particle:nth-child(5) {
    top: -30px;
    left: 25px;
    animation-delay: 1.6s;
}

.ph-title {
    font-size: 3.5rem;
    font-weight: 800;
    color: #1e293b;
    margin-bottom: 1rem;
    line-height: 1.1;
