.ph-description {
    font-size: 1.1rem;
    color: #64748b;
    max-width: 600px;
    margin: 0 auto;
    animation: fadeIn 0.8s ease-out 1s both;
}

.ph-card-wrapper {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 2rem;
    margin-top: 2rem;
    animation: fadeInUp 1s ease-out 1.2s both;
}

.ph-card-container {
    position: relative;
    display: inline-block;
}

