    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
    z-index: 1;
}

.ph-floating-element {
    position: absolute;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1), rgba(168, 85, 247, 0.1));
    border-radius: 50%;
    animation: float 6s ease-in-out infinite;
}

.ph-element-1 {
    width: 100px;
    height: 100px;
    top: 20%;
    left: 10%;
    animation-delay: 0s;
}

.ph-element-2 {
    width: 60px;
    height: 60px;
    top: 60%;
    right: 15%;
    animation-delay: 2s;
}

.ph-element-3 {
    width: 80px;
    height: 80px;
    bottom: 20%;
    left: 20%;
    animation-delay: 4s;
}

/* Animations */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
}

@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes rocketLaunch {
    0%, 100% {
        transform: translateY(0) rotate(0deg);
    }
    25% {
        transform: translateY(-10px) rotate(-5deg);
    }
    75% {
        transform: translateY(-5px) rotate(5deg);
    }
}

@keyframes particleFloat {
    0%, 100% {
        opacity: 0;
        transform: translateY(0);
    }
    50% {
        opacity: 1;
        transform: translateY(-20px);
    }
}

@keyframes textShimmer {
    0%, 100% {
        background-position: -200% center;
    }
    50% {
        background-position: 200% center;
    }
}

@keyframes rotateGlow {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

@keyframes shine {
    0% { left: -100%; }
    100% { left: 100%; }
}

@keyframes heartbeat {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.2);
    }
}

@keyframes float {
    0%, 100% {
        transform: translateY(0) rotate(0deg);
    }
    33% {
        transform: translateY(-20px) rotate(120deg);
    }
    66% {
        transform: translateY(10px) rotate(240deg);
    }
}

/* Responsive Design */
@media (max-width: 768px) {
    .ph-section-full {
        padding: 2rem 1rem;
    }

    .ph-title {
        font-size: 2.5rem;
    }

    .ph-title-line {
        font-size: 1.8rem;
    }

    .ph-subtitle {
        font-size: 1.2rem;
    }

    .ph-card {
        max-width: 100%;
        height: 380px;
    }

    .ph-card-wrapper {
        gap: 1.5rem;
    }

    .ph-rocket-icon {
        width: 48px;
        height: 48px;
    }

    .ph-floating-element {
        display: none;
    }
}

@media (max-width: 480px) {
    .ph-rocket-icon {
        width: 40px;
        height: 40px;
    }

    .ph-title {
        font-size: 2rem;
    }

    .ph-title-line {
        font-size: 1.5rem;
    }

    .ph-subtitle {
        font-size: 1rem;
    }

    .ph-card {
        height: 320px;
    }

    .ph-cta-content {
        flex-direction: column;
        gap: 0.25rem;
    }
}
