// Enhanced animations and interactions for Product Hunt section
document.addEventListener('DOMContentLoaded', function() {
    const phSection = document.querySelector('.ph-section-full');
    const phCard = document.querySelector('.ph-card');
    const phBadge = document.querySelector('.ph-badge');
    const phRocket = document.querySelector('.ph-rocket-icon');
    const phTitle = document.querySelector('.ph-title-highlight');

    // Intersection Observer for scroll-triggered animations
    const observerOptions = {
        threshold: 0.2,
        rootMargin: '0px 0px -100px 0px'
    };

    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('ph-in-view');

                // Trigger special effects when section comes into view
                setTimeout(() => {
                    triggerLaunchSequence();
                }, 500);
            }
        });
    }, observerOptions);

    if (phSection) {
        observer.observe(phSection);
    }

    // Launch sequence animation
    function triggerLaunchSequence() {
        if (phRocket) {
            phRocket.style.animation = 'rocketLaunch 3s ease-in-out infinite, rocketBoost 1s ease-out';
        }

        // Add sparkle effect to title
        if (phTitle) {
            phTitle.style.animation += ', sparkle 2s ease-in-out';
        }
    }

    // Enhanced card interactions
    if (phCard) {
        phCard.addEventListener('mouseenter', function() {
            this.style.transform = 'translateY(-12px) scale(1.02)';

            // Add subtle tilt based on mouse position
            phCard.addEventListener('mousemove', handleCardTilt);
        });

        phCard.addEventListener('mouseleave', function() {
            this.style.transform = '';
            phCard.removeEventListener('mousemove', handleCardTilt);
        });

        // Loading state handling
        phCard.addEventListener('load', function() {
            console.log('Product Hunt card loaded successfully');
            this.classList.add('ph-loaded');
        });

        phCard.addEventListener('error', function() {
            console.warn('Product Hunt card failed to load');
            showFallbackContent();
        });
    }

    // Card tilt effect
    function handleCardTilt(e) {
        const card = e.currentTarget;
        const rect = card.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        const centerX = rect.width / 2;
        const centerY = rect.height / 2;

        const rotateX = (y - centerY) / 20;
        const rotateY = (centerX - x) / 20;

        card.style.transform = `translateY(-12px) scale(1.02) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`;
    }

    // Enhanced badge interactions
    if (phBadge) {
        phBadge.addEventListener('click', function(e) {
            // Create ripple effect
            createRipple(e, this);

            // Analytics tracking
            trackPhClick('badge');

            // Visual feedback
            this.style.transform = 'scale(0.95) rotate(2deg)';
            setTimeout(() => {
                this.style.transform = '';
            }, 150);
        });

        // Periodic attention animation
        setInterval(() => {
            if (!phBadge.matches(':hover')) {
                phBadge.style.animation = 'pulse 0.6s ease-in-out';
                setTimeout(() => {
                    phBadge.style.animation = '';
                }, 600);
            }
        }, 15000);
    }

    // Create ripple effect
    function createRipple(event, element) {
        const ripple = document.createElement('div');
        const rect = element.getBoundingClientRect();
        const size = Math.max(rect.width, rect.height);
        const x = event.clientX - rect.left - size / 2;
        const y = event.clientY - rect.top - size / 2;

        ripple.style.cssText = `
            position: absolute;
            width: ${size}px;
            height: ${size}px;
            left: ${x}px;
            top: ${y}px;
            background: rgba(99, 102, 241, 0.3);
            border-radius: 50%;
            transform: scale(0);
            animation: ripple 0.6s ease-out;
            pointer-events: none;
        `;

        element.style.position = 'relative';
        element.style.overflow = 'hidden';
        element.appendChild(ripple);

        setTimeout(() => {
            ripple.remove();
        }, 600);
    }

    // Fallback content for failed iframe
    function showFallbackContent() {
        const fallback = document.createElement('div');
        fallback.className = 'ph-fallback';
        fallback.innerHTML = `
            <div style="
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: center;
                height: 100%;
                background: white;
                border-radius: 16px;
                border: 2px solid #e2e8f0;
                padding: 2rem;
                text-align: center;
            ">
                <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="#6366f1" stroke-width="2">
                    <path d="M13 2L3 14h9l-1 8 10-12h-9l1-8z"/>
                </svg>
                <h3 style="margin: 1rem 0 0.5rem 0; color: #1e293b;">Visit us on Product Hunt</h3>
                <p style="color: #64748b; margin-bottom: 1rem;">Check out Saski AI and show your support!</p>
                <a href="https://www.producthunt.com/products/saski-ai" target="_blank" rel="noopener" 
                   style="
                       background: #6366f1;
                       color: white;
                       padding: 0.75rem 1.5rem;
                       border-radius: 8px;
                       text-decoration: none;
                       font-weight: 500;
                       transition: background 0.3s ease;
                   "
                   onmouseover="this.style.background='#4f46e5'"
                   onmouseout="this.style.background='#6366f1'">
                    View on Product Hunt
                </a>
            </div>
        `;

        if (phCard && phCard.parentNode) {
            phCard.parentNode.replaceChild(fallback, phCard);
        }
    }

    // Analytics tracking
    function trackPhClick(element) {
        console.log(`Product Hunt ${element} clicked`);

        // Google Analytics 4 event tracking
        if (typeof gtag !== 'undefined') {
            gtag('event', 'product_hunt_click', {
                'element_type': element,
                'event_category': 'engagement',
                'event_label': 'Product Hunt Section'
            });
        }

        // Custom analytics or other tracking services can be added here
    }

    // Parallax effect for floating elements
    if (window.innerWidth > 768) {
        window.addEventListener('scroll', throttle(handleParallax, 16));
    }

    function handleParallax() {
        const scrolled = window.pageYOffset;
        const parallaxElements = document.querySelectorAll('.ph-floating-element');

        parallaxElements.forEach((element, index) => {
            const speed = 0.5 + (index * 0.2);
            const yPos = -(scrolled * speed);
            element.style.transform = `translateY(${yPos}px)`;
        });
    }

    // Throttle function for performance
    function throttle(func, limit) {
        let inThrottle;
        return function() {
            const args = arguments;
            const context = this;
            if (!inThrottle) {
                func.apply(context, args);
                inThrottle = true;
                setTimeout(() => inThrottle = false, limit);
            }
        }
    }

    // Add additional CSS animations dynamically
    const additionalCSS = `
        @keyframes pulse {
            0%, 100% { transform: scale(1); }
            50% { transform: scale(1.05); }
        }

        @keyframes ripple {
            to {
                transform: scale(2);
                opacity: 0;
            }
        }

        @keyframes rocketBoost {
            0% { transform: translateY(0) scale(1); }
            50% { transform: translateY(-20px) scale(1.1); }
            100% { transform: translateY(0) scale(1); }
        }

        @keyframes sparkle {
            0%, 100% { filter: brightness(1); }
            50% { filter: brightness(1.2) contrast(1.1); }
        }

        .ph-loaded {
            animation: cardReveal 0.8s ease-out;
        }

        @keyframes cardReveal {
            from {
                opacity: 0;
                transform: translateY(20px) scale(0.95);
            }
            to {
                opacity: 1;
                transform: translateY(0) scale(1);
            }
        }
    `;

    // Inject additional CSS
    if (!document.querySelector('#ph-enhanced-css')) {
        const style = document.createElement('style');
        style.id = 'ph-enhanced-css';
        style.textContent = additionalCSS;
        document.head.appendChild(style);
    }
});

